#include <linux/fs.h>
#include <linux/miscdevice.h>
#include <linux/mm.h>
#include <linux/module.h>
#include <linux/version.h>
#include <linux/kallsyms.h>
#include <asm/uaccess.h>
#include <asm/tlbflush.h>
#include "leaky.h"

MODULE_AUTHOR("Michael Schwarz");
MODULE_DESCRIPTION("Device to simply load data in the kernel");
MODULE_LICENSE("GPL");

#if LINUX_VERSION_CODE >= KERNEL_VERSION(4, 11, 0)
#define from_user raw_copy_from_user
#define to_user raw_copy_to_user
#else
#define from_user copy_from_user
#define to_user copy_to_user
#endif

static bool device_busy = false;

static void maccess(void *p) { asm volatile("movq (%0), %%rax\n" : : "c"(p) : "rax"); }


static const char* leaky_data = "";


static int device_open(struct inode *inode, struct file *file) {
  /* Check if device is busy */
  if (device_busy == true) {
    return -EBUSY;
  }

  /* Lock module */
  try_module_get(THIS_MODULE);

  device_busy = true;

  return 0;
}

static int device_release(struct inode *inode, struct file *file) {
  /* Unlock module */
  device_busy = false;

  module_put(THIS_MODULE);

  return 0;
}


static long device_ioctl(struct file *file, unsigned int ioctl_num, unsigned long ioctl_param) {
  switch (ioctl_num) {
    case LEAKY_IOCTL_CMD_CACHE:
    {
        int j;
        for(j = 0; j < 100; j++) maccess((void*)(leaky_data + j * 64)); 
        return 0;
    }
    default:
        return -1;
  }

  return 0;
}

static struct file_operations f_ops = {.unlocked_ioctl = device_ioctl,
                                       .open = device_open,
                                       .release = device_release};

static struct miscdevice misc_dev = {
    .minor = MISC_DYNAMIC_MINOR,
    .name = LEAKY_DEVICE_NAME,
    .fops = &f_ops,
    .mode = S_IRWXUGO,
};

int init_module(void) {
  int r;

  /* Register device */
  r = misc_register(&misc_dev);
  if (r != 0) {
    printk(KERN_ALERT "[leaky-module] Failed registering device with %d\n", r);
    return 1;
  }

  printk(KERN_INFO "[leaky-module] Loaded.\n");

  return 0;
}

void cleanup_module(void) {
  misc_deregister(&misc_dev);

  printk(KERN_INFO "[leaky-module] Removed.\n");
}


